#include <iostream>
#include <string>
#include <vector>
#include <unordered_map>

#include <random>
using namespace std;

void printVector(vector<int> v) {
    for (int i = 0; i < v.size(); i++) {
        cout << v[i] << ' ';
    }
}

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> v;
        int num = (int)words.size();
        if (num == 0)
            return v;

        unordered_map<string, int> dict;
        dict.reserve(num);
        for (int i = 0; i < num; i++) {
            dict[words[i]]++;
        }

        int len = (int)s.size();
        int size = (int)words[0].size();
        int sublen = size * num;

        for (int j = 0; j < size; j++) {
            unordered_map<string, int> tdict;
            int left = j;

            for (int i = j; i <= len - size; i += size) {
                string word = s.substr(i, size);
                //cout << "left="<<left<<", j="<<j<<", i="<<i<<", word="<<word<<'\n';
                if (dict.find(word) != dict.end()) {
                    tdict[word]++;

                    if (tdict[word] > dict[word]) {
                        while (tdict[word] > dict[word]) {
                            tdict[s.substr(left, size)]--;
                            left += size;
                        }
                    } else if (i-left == sublen - size) {
                        v.push_back(left);
                        tdict[s.substr(left, size)]--;
                        left += size;
                    }
                } else {
                    left = i + size;
                    tdict.clear();
                }
            }
        }
        for (auto it = dict.begin(); it != dict.end(); it++) {
            cout << it->first << " -> " << it->second << '\n';
        }
        return v;
    }

    double hash(double f, double code[], string &word) {
        double result = 0.;
        for (auto &c : word) result = result * f + code[c];
        return result;
    }
    vector<int> findSubstring2(string s, vector<string> &words) {
        uniform_real_distribution<double> unif(0., 1.);
        default_random_engine seed;
        double code[128];
        for (auto &d : code) d = unif(seed);
        double f = unif(seed) / 5. + 0.8;
        double value = 0;

        // The complexity of the following for loop is O(L.size( ) * nW).
        for (auto &str : words) value += log(hash(f, code, str));

        int unit = 1e9;
        int key = (value-floor(value))*unit;
        int len = (int)s.size(), num = (int)words.size(), size = (int)words[0].size();
        double fn = pow(f, size-1.);
        vector<int> result;
        if (len < size) return result;
        vector<double> values(len-size+1);
        string word(s.begin(), s.begin()+size);
        values[0] = hash(f, code, word);

        // Use a moving window to hash every word with length nW in S to a float number,
        // which is stored in vector values[]
        // The complexity of this step is O(nS).
        for (int i=1; i<=len-size; ++i) values[i] = (values[i-1] - code[s[i-1]]*fn)*f + code[s[i+size-1]];

        // This for loop will run nW times, each iteration has a complexity O(nS/nW)
        // So the overall complexity is O(nW * (nS / nW)) = O(nS)
        for (int i=0; i<size; ++i) {
            int j0=i, j1=i, k=0;
            double sum = 0.;

            // Use a moving window to hash every L.size() continuous words with length nW in S.
            // This while loop will terminate within nS/nW iterations since the increasement of j1 is nW,
            // So the complexity of this while loop is O(nS / nW).
            while(j1<=len-size) {
                sum += log(values[j1]);
                ++k;
                j1 += size;
                if (k==num) {
                    int key1 = (sum-floor(sum)) * unit;
                    if (key1==key) result.push_back(j0);
                    sum -= log(values[j0]);
                    --k;
                    j0 += size;
                }
            }
        }
        return result;
    }


};

