#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    string shortestPalindrome(string s) {
        if (s.size() <= 1)
            return s;
        string T = "^#";
        for (int i = 0; i < s.size(); i++)
            T += s.substr(i,1) + "#";
        T += "$";

        int P[T.size()], R = 0, C = 0, maxL = 0;
        P[0] = 0;
        for (int i = 1; i < T.size()-1; i++) {
            P[i] = i >= R ? 0 : min(R-i, P[2*C-i]);
            while (T[i-P[i]-1] == T[i+P[i]+1])
                P[i]++;
            if (i+P[i] > R)
                R = (C = i) + P[i];
            if (i-P[i]-1 == 0)
                maxL = P[i];
        }
        string ret = s.substr(maxL);
        reverse(ret.begin(), ret.end());
        return ret+s;
    }
};

int main(int arg, char *argv[]) {
    // insert code here...
    cout << "LeetCode 214. Shortest Palindrome, C++ ...\n\n";
    Solution sol;

    string s = "abaa";
    //s = "";
    
    string r = sol.shortestPalindrome(s);
    cout << r << '\n';
    
    return 0;
}